#include "framework_dependent/fw.h"#include "engine2d/resources.h"#include "config_events.h"extern bool PEvents[];extern bool KEvents[];extern bool GEvents[];void attendProgEvents( _EVENT* event ) {  if( PEvents[ EVENT_TILESET_ANIMATION ] ) {    tileSeq* t = (tileSeq*)event->user.data1;	t->draw();	PEvents[ EVENT_TILESET_ANIMATION ] = false;	return;  }  if( PEvents[ EVENT_MOVE_OBJ ] ) {    visObj* v_obj = (visObj*)event->user.data1;	v_obj->deltaMove();    v_obj->draw();	PEvents[ EVENT_MOVE_OBJ ] = false;  }}